// Add a custom label to mark emails as processed
const email = msg.payload;

// Create a label name for processed emails
const processedLabel = 'Processed by Node-RED';

// Store the original email data for labeling
msg.emailData = {
    messageId: email.messageId || email.id,
    label: processedLabel
};

// Add notification about labeling
msg.notification = `${msg.notification} (Adding label: ${processedLabel})`;

return msg;

// Categorize incoming emails and route to appropriate agents
const email = msg.payload.email;
const subject = email.subject.toLowerCase();
const body = email.body.toLowerCase();
const from = email.from.toLowerCase();

// Determine email category and priority
let category = 'general';
let priority = 'low';

// Support emails
if (subject.includes('support') || subject.includes('help') || 
    subject.includes('issue') || subject.includes('problem') ||
    subject.includes('broken') || subject.includes('fix')) {
    category = 'support';
}

// Sales emails
else if (subject.includes('quote') || subject.includes('pricing') || 
         subject.includes('demo') || subject.includes('sales') ||
         subject.includes('purchase') || subject.includes('buy')) {
    category = 'sales';
}

// Technical emails
else if (subject.includes('technical') || subject.includes('bug') || 
         subject.includes('error') || subject.includes('api') ||
         subject.includes('code') || subject.includes('development')) {
    category = 'technical';
}

// Urgent emails
if (subject.includes('urgent') || subject.includes('emergency') || 
    subject.includes('critical') || subject.includes('asap') ||
    subject.includes('immediate')) {
    priority = 'high';
    if (category === 'general') category = 'urgent';
}

// Important emails
else if (subject.includes('important') || subject.includes('priority')) {
    priority = 'medium';
}

msg.payload = {
    email: email,
    category: category,
    priority: priority,
    messageId: msg.payload.messageId,
    timestamp: msg.payload.timestamp,
    agentType: msg.payload.agentType
};

// Add routing information
msg.routing = {
    support: category === 'support',
    sales: category === 'sales',
    technical: category === 'technical',
    urgent: category === 'urgent',
    general: category === 'general'
};

return msg;

// Support Agent - Handles customer support inquiries
const email = msg.payload.email;
const priority = msg.payload.priority;

// Generate support response
const response = {
    to: email.from,
    subject: `Re: ${email.subject}`,
    priority: priority,
    agent: 'support_agent',
    template: 'support_response',
    variables: {
        customerName: email.from.split('@')[0],
        originalSubject: email.subject,
        ticketId: `SUPPORT-${Date.now()}`,
        priority: priority
    }
};

// Add support-specific logic
if (priority === 'high') {
    response.escalate = true;
    response.escalateTo = 'escalation_agent';
}

msg.payload = response;
msg.notification = `ðŸ“§ Support Agent processed: ${email.subject} (${priority} priority)`;

return msg;

