const selectedTemplate = templates[template];

msg.payload = {
    to: response.to,
    subject: selectedTemplate.subject,
    text: selectedTemplate.text,
    html: selectedTemplate.text.replace(/
/g, '<br>'),
    agent: response.agent,
    priority: response.priority,
    template: template,
    variables: variables,
    urgent: response.urgent || false,
    escalate: response.escalate || false
};

return msg;

// Log email response and update statistics
const response = msg.payload;
const emailData = msg.payload;

// Create log entry
const logEntry = {
    timestamp: new Date().toISOString(),
    agent: emailData.agent,
    to: emailData.to,
    subject: emailData.subject,
    priority: emailData.priority,
    template: emailData.template,
    urgent: emailData.urgent,
    escalate: emailData.escalate
};

// Save to file
const fs = require('fs');
const logFile = 'email_responses.log';
fs.appendFileSync(logFile, JSON.stringify(logEntry, null, 2) + '\n---\n');

msg.payload = {
    success: true,
    logEntry: logEntry,
    notification: `âœ… Email sent by ${emailData.agent}: ${emailData.subject}`
};

return msg;

msg.payload = {
